import React, { useEffect } from 'react';
import './homepage.css';
import Estimation from '../components/Estimation';
import WhyChooseUs from '../components/WhyChooseUs';

const Homepage = () => {

    useEffect(() => {
        const sections = document.querySelectorAll('.section-to-observe');

        const observer = new IntersectionObserver((entries) => {
            entries.forEach(entry => {
                if (entry.isIntersecting) {
                    entry.target.classList.add('reveal');
                }
            });
        }, {
            threshold: 0.1,
        });

        sections.forEach(section => {
            observer.observe(section);
        });

        return () => {
            sections.forEach(section => observer.unobserve(section));
        };
    }, []);

    return (
        <div className="homepage">
            <div className="hero section-to-observe">
                <h1>Empower your energy savings today.</h1>
                <p>Take control of your energy consumption with personalized insights, cost-saving tools, and tailored solutions. Make every watt count towards a greener, more affordable future.</p>
                <div className="arrow-down">
                    <span>&#9662;</span>
                </div>
            </div>

            <div id="estimation-section" className="section-to-observe">
                <Estimation />
            </div>

            <div id="why-choose-us-section" className="section-to-observe">
                <WhyChooseUs />
            </div>
        </div>
    );
};

export default Homepage;
