import React from 'react';
import './Estimation.css';

const Estimation = () => {

    // Add this JavaScript code in your main JS file or within a <script> tag in your HTML
    document.addEventListener('DOMContentLoaded', () => {
        const h1 = document.querySelector('.info-section h1');

        const observer = new IntersectionObserver(entries => {
            entries.forEach(entry => {
                if (entry.isIntersecting) {
                    h1.classList.add('reveal'); // Use 'reveal' or 'reveal-delayed'
                    observer.disconnect(); // Stop observing once the animation has triggered
                }
            });
        });

        observer.observe(h1);
    });


    return (
        <div className="section-two">
            <div className="image-section"></div>
            <div className="info-section">
                <div className="content">
                    <h1>Identify Your Homeâ€™s Energy Consumption</h1>
                    <div className="stats">
                        <div className="stat-item">
                            <h2>Average Home Consumption</h2>
                            <p>3000Kwh/M</p>
                        </div>
                        <div className="stat-item">
                            <h2>Average Bill Cost</h2>
                            <p>$542.27/M</p>
                        </div>
                    </div>
                    <p className="description">Upload images of your home to discover which appliances are driving up your energy bills. Our advanced image detection tool will analyze your energy consumption and provide tailored tips to help you save.</p>
                    <button className="start-button">Start Now</button>
                </div>
            </div>
        </div>
    );
}

export default Estimation;
